import { CdkAccordion } from '@angular/cdk/accordion';
import { NgTemplateOutlet } from '@angular/common';
import {
  ChangeDetectionStrategy,
  Component,
  contentChildren,
} from '@angular/core';
import {
  AccordionDirective,
  AccordionItemDirective,
} from '@ngx-uk-frontend/core/accordion';
import { GovUKAccordionItemComponent } from '../accordion-item/accordion-item.component';

/**
 * This component implements the GOV.UK Design System accordion component.
 * It allows users to show and hide sections of related content on a page.
 *
 * @see https://design-system.service.gov.uk/components/accordion/
 */
@Component({
  imports: [NgTemplateOutlet],
  selector: 'ngx-govuk-accordion',
  templateUrl: './accordion.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  hostDirectives: [CdkAccordion],
})
export class GovUKAccordionComponent extends AccordionDirective {
  /** Collection of accordion item components that are children of this accordion. */
  readonly items = contentChildren(GovUKAccordionItemComponent);

  /**
   * Implementation of the abstract getItems method.
   * Returns the accordion items for use by the base directive.
   */
  protected override getItems(): readonly AccordionItemDirective[] {
    return this.items();
  }
}

import {
  ChangeDetectionStrategy,
  Component,
  TemplateRef,
  viewChild,
} from '@angular/core';
import { AccordionItemDirective } from '@ngx-uk-frontend/core/accordion';

/**
 * This component implements a section in the GOV.UK Design System accordion component.
 * It contains the heading, optional summary, and content for an accordion section.
 *
 * @see https://design-system.service.gov.uk/components/accordion/
 */
@Component({
  selector: 'ngx-govuk-accordion-item',
  templateUrl: './accordion-item.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKAccordionItemComponent extends AccordionItemDirective {
  /** Reference to the template containing the accordion item's content. */
  readonly content = viewChild.required<TemplateRef<unknown>>('content');
}

import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  input,
} from '@angular/core';
import { RouterLink } from '@angular/router';

/**
 * This component implements the GOV.UK Design System back link component.
 * It provides a consistent way to help users navigate back to the previous page.
 *
 * @see https://design-system.service.gov.uk/components/back-link/
 */
@Component({
  selector: 'ngx-govuk-back-link',
  imports: [RouterLink],
  templateUrl: './back-link.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKBackLinkComponent {
  readonly link = input.required<RouterLink['routerLink']>();

  readonly inverse = input(false, { transform: booleanAttribute });
}

import {
  ChangeDetectionStrategy,
  Component,
  contentChildren,
} from '@angular/core';
import { RouterLink } from '@angular/router';
import { BreadcrumbsDirective } from '@ngx-uk-frontend/core/breadcrumbs';
import { GovUKBreadcrumbDirective } from './breadcrumb/breadcrumb.directive';

/**
 * This component implements the GOV.UK Design System breadcrumbs component.
 * It helps users understand where they are in a website's structure and navigate back to previous pages.
 *
 * @see https://design-system.service.gov.uk/components/breadcrumbs/
 */
@Component({
  selector: 'ngx-govuk-breadcrumbs',
  imports: [RouterLink],
  templateUrl: './breadcrumbs.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
})
export class GovUKBreadcrumbsComponent extends BreadcrumbsDirective {
  /** Collection of GOV.UK breadcrumb items that are children of this component. */
  override readonly items = contentChildren(GovUKBreadcrumbDirective);
}

import { NgClass } from '@angular/common';
import {
  booleanAttribute,
  ChangeDetectionStrategy,
  Component,
  input,
} from '@angular/core';
import { ButtonDirective } from '@ngx-uk-frontend/core/button';

/**
 * The color type for GovUK buttons
 * @typedef {('primary'|'secondary'|'warning'|'inverse')} GovUKButtonColorType
 * - 'primary' - Default blue button
 * - 'secondary' - Grey button for secondary actions
 * - 'warning' - Red warning button
 * - 'inverse' - White button on dark background
 */
export type GovUKButtonColorType =
  | 'primary'
  | 'secondary'
  | 'warning'
  | 'inverse';

/**
 * This component implements the GOV.UK Design System button component.
 * It allows users to take actions, submit forms, or navigate to a new page.
 *
 * @see https://design-system.service.gov.uk/components/button/
 */
@Component({
  selector: 'ngx-govuk-button',
  imports: [NgClass],
  templateUrl: './button.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKButtonComponent extends ButtonDirective {
  /** Whether to show the start icon (forward arrow). Defaults to false. */
  readonly start = input(false, { transform: booleanAttribute });

  /**
   * The color variant of the button. Defaults to 'primary'.
   * Can be 'primary', 'secondary', 'warning', or 'inverse'.
   */
  readonly color = input<GovUKButtonColorType>('primary');
}

import { ChangeDetectionStrategy, Component } from '@angular/core';

/**
 * This component implements the GOV.UK Design System button group component.
 * It provides a way to group buttons together in a form with consistent spacing.
 *
 * @see https://design-system.service.gov.uk/components/button/
 */
@Component({
  selector: 'ngx-govuk-button-group',
  templateUrl: './button-group.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKButtonGroupComponent {}

import { ChangeDetectionStrategy, Component } from '@angular/core';
import { ReactiveFormsModule } from '@angular/forms';
import { CheckboxDirective } from '@ngx-uk-frontend/core/checkbox';
import { CommonFormInputDirective } from '@ngx-uk-frontend/core/form-utils';
import {
  inputCommonInputs,
  ValueAccessorDirective,
} from 'ngx-govuk-frontend/form-utils';

/**
 * This component implements the GOV.UK Design System checkbox component.
 * It allows users to select one or more options by ticking a box.
 *
 * @see https://design-system.service.gov.uk/components/checkboxes/
 */
@Component({
  selector: 'ngx-govuk-checkbox',
  imports: [ReactiveFormsModule],
  templateUrl: './checkbox.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  hostDirectives: [
    ValueAccessorDirective,
    { directive: CommonFormInputDirective, inputs: inputCommonInputs },
  ],
})
export class GovUKCheckboxComponent extends CheckboxDirective {}

import {
  ChangeDetectionStrategy,
  Component,
  input,
  output,
} from '@angular/core';
import {
  GovUKButtonComponent,
  GovUKButtonGroupComponent,
} from 'ngx-govuk-frontend/button';

/**
 * This component implements the GOV.UK Design System cookie banner component.
 * It informs users that your service uses cookies and allows them to accept or reject cookies.
 *
 * @see https://design-system.service.gov.uk/components/cookie-banner/
 */
@Component({
  selector: 'ngx-govuk-cookie-banner',
  imports: [GovUKButtonComponent, GovUKButtonGroupComponent],
  templateUrl: './cookie-banner.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKCookieBannerComponent {
  /** The name of the application or service using the cookie banner. This input is required. */
  readonly appName = input.required<string>();

  /** Event emitted when the user accepts cookies. */
  readonly accept = output<void>();

  /** Event emitted when the user rejects cookies. */
  readonly reject = output<void>();

  /** Event emitted when the user chooses to view cookie settings. */
  readonly view = output<void>();
}

import { ChangeDetectionStrategy, Component, output } from '@angular/core';
import {
  GovUKButtonComponent,
  GovUKButtonGroupComponent,
} from 'ngx-govuk-frontend/button';

/**
 * This component implements the confirmation message within the GOV.UK Design System cookie banner.
 * It displays after the user accepts or rejects cookies.
 *
 * @see https://design-system.service.gov.uk/components/cookie-banner/
 */
@Component({
  selector: 'ngx-govuk-cookie-confirmation',
  imports: [GovUKButtonComponent, GovUKButtonGroupComponent],
  templateUrl: './cookie-confirmation.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class GovUKCookieConfirmationComponent {
  /** Event emitted when the user wants to change their cookie settings. */
  readonly changeSettings = output<void>();

  /** Event emitted when the user wants to hide the confirmation banner. */
  readonly hide = output<void>();
}

