{
  render: () => {
    const formControl = new FormControl('', Validators.required);
    const form = new FormGroup({ input: formControl });

    return {
      props: {
        form,
        errorMessage: 'Please enter a value',
        markAsTouched: () => {
          formControl.markAsTouched();
        },
      },
      template: `<form [formGroup]="form">
        <ngx-govuk-form-group [label]="'Enter a value'" [labelFor]="'required'" [errorMessage]="form.get('input')?.touched && form.get('input')?.errors ? errorMessage : ''">
          <ngx-govuk-text-input
            formControlName="input"
            inputId="required"
          ></ngx-govuk-text-input>
        </ngx-govuk-form-group>
        <button (click)="markAsTouched()" type="button" class="govuk-button">Submit</button>
      </form>`,
    };
  },
  parameters: {
    docs: {
      description: {
        story:
          'A required text input that shows validation error when submitted empty.',
      },
    },
  },
}

{
  render: () => {
    const formControl = new FormControl('', Validators.required);
    const form = new FormGroup({ input: formControl });

    return {
      props: {
        form,
        inputId: 'required',
        getErrorMessage: () => {
          return formControl.touched && formControl.errors?.['required']
            ? 'Please enter your feedback'
            : '';
        },
        markAsTouched: () => {
          formControl.markAsTouched();
        },
      },
      template: `<form [formGroup]="form">
        <ngx-govuk-form-group 
          [label]="'Your feedback'" 
          [labelFor]="inputId"
          [errorMessage]="getErrorMessage()">
          <ngx-govuk-textarea
            formControlName="input"
            [inputId]="inputId"
            [rows]="5"
          ></ngx-govuk-textarea>
        </ngx-govuk-form-group>
        <button (click)="markAsTouched()" type="button" class="govuk-button">Submit</button>
      </form>`,
    };
  },
  parameters: {
    docs: {
      description: {
        story: 'A required textarea with validation.',
      },
    },
  },
}

